async function importExistingMappings() {
  console.log('üì• –ò–º–ø–æ—Ä—Ç —Å—É—â–µ—Å—Ç–≤—É—é—â–∏—Ö –º–∞–ø–ø–∏–Ω–≥–æ–≤ –≤ –Ω–æ–≤—É—é —Ç–∞–±–ª–∏—Ü—É...')

  try {
    // –û—á–∏—â–∞–µ–º —Ç–∞–±–ª–∏—Ü—É
    await prisma.itemNameMapping.deleteMany()
    console.log('üßπ –¢–∞–±–ª–∏—Ü–∞ –æ—á–∏—â–µ–Ω–∞')

    // –î–æ–±–∞–≤–ª—è–µ–º –º–∞–ø–ø–∏–Ω–≥–∏ –ø–æ—Ä—Ü–∏—è–º–∏
    const batchSize = 20
    let importedCount = 0

    for (let i = 0; i < existingMappings.length; i += batchSize) {
      const batch = existingMappings.slice(i, i + batchSize)
      
      await prisma.itemNameMapping.createMany({
        data: batch,
        skipDuplicates: true
      })

      importedCount += batch.length
      console.log(`üì¶ –ò–º–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–æ ${importedCount}/${existingMappings.length} –º–∞–ø–ø–∏–Ω–≥–æ–≤...`)
    }

    console.log(`‚úÖ –ò–º–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à—ë–Ω! –í—Å–µ–≥–æ –¥–æ–±–∞–≤–ª–µ–Ω–æ: ${importedCount} –º–∞–ø–ø–∏–Ω–≥–æ–≤`)

    // –ü–æ–∫–∞–∑–∞—Ç—å —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É
    const stats = await prisma.itemNameMapping.groupBy({
      by: ['game', 'itemType'],
      _count: {
        id: true
      },
      orderBy: [
        { game: 'asc' },
        { itemType: 'asc' }
      ]
    })

    console.log('\nüìä –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –∏–º–ø–æ—Ä—Ç–∞:')
    stats.forEach(stat => {
      console.log(`   ${stat.game} ${stat.itemType}: ${stat._count.id} –ø—Ä–µ–¥–º–µ—Ç–æ–≤`)
    })

  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –∏–º–ø–æ—Ä—Ç–∞:', error)
  } finally {
    await prisma.$disconnect()
  }
}

importExistingMappings()
